mapper.select:
SELECT FILE_CODE, DOC_CODE, ORIGIN_NAME, UPLOAD_NAME FROM APPROVAL_FILE
WHERE DOC_CODE = #{docCode}
ORDER BY FILE_CODE ASC

SELECT APPR_SEQ, APPR_STATUS FROM APPROVAL_LIST
WHERE APPR_SEQ = 2
AND DOC_CODE = #{docCode}

mapper.update:
UPDATE APPROVAL_DOC SET DOC_TITLE = #{docTitle}, PERIOD_CODE = #{periodCode}, DOC_UPDATED = SYSDATE
WHERE DOC_CODE = #{docCode}
AND DOC_STATUS = 'N'

UPDATE APPROVAL_LIST SET APPR_STATUS = 'A', APPR_DATE = SYSDATE
WHERE DOC_CODE = #{docCode}
AND APPR_EMP_CODE = #{apprEmpCode}
AND APPR_SEQ = #{apprSeq}

mapper.select:
SELECT MAX(APPR_SEQ) AS MAX_SEQ
FROM APPROVAL_LIST
WHERE DOC_CODE = #{docCode}

mapper.update:
UPDATE APPROVAL_LIST SET APPR_STATUS = 'C', APPR_DATE = SYSDATE
WHERE DOC_CODE = #{docCode}
AND APPR_EMP_CODE = #{apprEmpCode}
AND APPR_SEQ = #{apprSeq}

UPDATE APPROVAL_LIST SET APPR_STATUS = 'C', APPR_DATE = SYSDATE
WHERE DOC_CODE = #{docCode}
AND APPR_STATUS = 'W'

mapper.select:
SELECT A.DOC_CODE, A.PERIOD_CODE, A.DOC_FORM_CODE, A.EMP_CODE, A.DOC_TITLE, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, A.DOC_APPR_DATE, E.EMP_NAME, F.FORM_NAME FROM APPROVAL_DOC A INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE INNER JOIN DOC_FORM F ON F.FORM_CODE = A.DOC_FORM_CODE WHERE E.DEPT_CODE = #{deptCode} AND A.DOC_APPR_DATE IS NULL AND A.DOC_STATUS = 'N'
AND A.DOC_FORM_CODE = #{docFormCode}
ORDER BY A.DOC_CODE DESC

SELECT DISTINCT(A.DOC_CODE) AS DOC_CODE, A.DOC_FORM_CODE, A.DOC_TITLE, A.EMP_CODE, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, E.EMP_NAME, F.FORM_NAME FROM APPROVAL_DOC A INNER JOIN DOC_FORM F ON A.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE INNER JOIN APPROVAL_LIST L ON L.DOC_CODE = A.DOC_CODE WHERE L.APPR_STATUS = ANY (SELECT L.APPR_STATUS FROM APPROVAL_LIST L WHERE L.APPR_STATUS = 'W') AND L.APPR_EMP_CODE = #{empCode} AND A.DOC_STATUS = 'N'
AND A.DOC_FORM_CODE = #{docFormCode}
ORDER BY A.DOC_CODE DESC

SELECT COUNT(*) FROM APPROVAL_DOC A 
INNER JOIN APPROVAL_REF R ON A.DOC_CODE = R.DOC_CODE 
WHERE R.REF_EMP_CODE = #{empCode} 
AND A.DOC_APPR_DATE IS NULL	

	AND A.DOC_FORM_CODE = #{docFormCode}

SELECT DISTINCT(A.DOC_CODE) AS DOC_CODE, DF.FORM_NAME, A.DOC_TITLE, A.EMP_CODE, E.EMP_NAME, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE FROM APPROVAL_DOC A, (SELECT F.DOC_CODE, F.REF_EMP_CODE FROM APPROVAL_REF F) F, (SELECT DF.FORM_CODE, DF.FORM_NAME FROM DOC_FORM DF) DF, (SELECT E.EMP_CODE, E.EMP_NAME FROM EMP E) E, (SELECT * FROM APPROVAL_LIST L) L WHERE A.DOC_CODE = F.DOC_CODE AND A.EMP_CODE = E.EMP_CODE AND A.DOC_FORM_CODE = DF.FORM_CODE AND L.APPR_STATUS = ANY (SELECT L.APPR_STATUS FROM APPROVAL_LIST L WHERE L.APPR_STATUS = 'W') AND L.DOC_CODE = F.DOC_CODE AND A.DOC_STATUS = 'N' AND F.REF_EMP_CODE = #{empCode}
AND A.DOC_FORM_CODE = #{docFormCode}
ORDER BY A.DOC_CODE DESC

WITH T1 AS (SELECT A.DOC_CODE, A.DOC_TITLE, A.DOC_FORM_CODE, L.APPR_SEQ, L.APPR_STATUS , F.FORM_NAME, E.EMP_NAME, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, A.DOC_STATUS FROM APPROVAL_DOC A INNER JOIN APPROVAL_LIST L ON A.DOC_CODE = L.DOC_CODE INNER JOIN DOC_FORM F ON A.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE WHERE L.APPR_STATUS = 'W' AND A.DOC_STATUS = 'N' AND L.APPR_EMP_CODE = #{empCode}) SELECT T1.DOC_CODE, T1.DOC_FORM_CODE, T1.DOC_TITLE, T1.EMP_NAME, T1.FORM_NAME, T1.DOC_STATUS, T1.DOC_ENROLL_DATE FROM T1 INNER JOIN APPROVAL_LIST L ON T1.DOC_CODE = L.DOC_CODE WHERE L.DOC_CODE = T1.DOC_CODE AND L.APPR_SEQ = T1.APPR_SEQ-1 AND L.APPR_STATUS = 'A'
AND T1.DOC_FORM_CODE = #{docFormCode}
ORDER BY T1.DOC_CODE DESC

WITH T1 AS (SELECT A.DOC_CODE, A.DOC_TITLE, A.DOC_FORM_CODE, L.APPR_SEQ, L.APPR_STATUS , F.FORM_NAME, E.EMP_NAME, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, A.DOC_STATUS FROM APPROVAL_DOC A INNER JOIN APPROVAL_LIST L ON A.DOC_CODE = L.DOC_CODE INNER JOIN DOC_FORM F ON A.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE WHERE L.APPR_STATUS = 'W' AND A.DOC_STATUS = 'N' AND A.EMP_CODE = #{empCode}) SELECT COUNT(DISTINCT(T1.DOC_CODE)) FROM T1 INNER JOIN APPROVAL_LIST L ON T1.DOC_CODE = L.DOC_CODE WHERE L.DOC_CODE = T1.DOC_CODE
AND T1.DOC_FORM_CODE = #{docFormCode}
ORDER BY T1.DOC_CODE DESC

WITH T1 AS (SELECT A.DOC_CODE, A.DOC_TITLE, A.DOC_FORM_CODE, L.APPR_SEQ, L.APPR_STATUS , F.FORM_NAME, E.EMP_NAME, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, A.DOC_STATUS FROM APPROVAL_DOC A INNER JOIN APPROVAL_LIST L ON A.DOC_CODE = L.DOC_CODE INNER JOIN DOC_FORM F ON A.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE WHERE L.APPR_STATUS = 'W' AND A.DOC_STATUS = 'N' AND A.EMP_CODE = #{empCode}) SELECT DISTINCT(T1.DOC_CODE) AS DOC_CODE, T1.DOC_FORM_CODE, T1.DOC_TITLE, T1.EMP_NAME, T1.FORM_NAME, T1.DOC_STATUS, T1.DOC_ENROLL_DATE FROM T1 INNER JOIN APPROVAL_LIST L ON T1.DOC_CODE = L.DOC_CODE WHERE L.DOC_CODE = T1.DOC_CODE
AND T1.DOC_FORM_CODE = #{docFormCode}
ORDER BY T1.DOC_CODE DESC

SELECT A.DOC_CODE, A.PERIOD_CODE, A.DOC_FORM_CODE, A.EMP_CODE, A.DOC_TITLE, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, A.DOC_APPR_DATE, E.EMP_NAME, F.FORM_NAME , ( SELECT CASE WHEN SB_AD.DOC_APPR_DATE IS NULL THEN '진행중' WHEN (SELECT COUNT(*) FROM APPROVAL_LIST WHERE DOC_CODE = A.DOC_CODE AND APPR_STATUS = 'C') > 0 THEN '반려' ELSE '완료' END FROM APPROVAL_DOC SB_AD WHERE SB_AD.DOC_CODE = A.DOC_CODE ) AS STATUS FROM APPROVAL_DOC A INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE INNER JOIN DOC_FORM F ON F.FORM_CODE = A.DOC_FORM_CODE WHERE E.DEPT_CODE = 2 AND A.DOC_APPR_DATE IS NOT NULL
AND A.DOC_FORM_CODE = #{docFormCode}
ORDER BY A.DOC_CODE DESC

SELECT COUNT(*) FROM APPROVAL_DOC
WHERE EMP_CODE = #{empCode}
AND DOC_APPR_DATE IS NOT NULL

	AND DOC_FORM_CODE = #{docFormCode}

SELECT D.DOC_CODE, D.PERIOD_CODE, D.DOC_FORM_CODE, D.EMP_CODE, D.DOC_TITLE, TO_CHAR(D.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, TO_CHAR(D.DOC_UPDATED, 'YYYY.MM.DD') AS DOC_UPDATED, D.DOC_STATUS, F.FORM_NAME, E.EMP_NAME , ( SELECT CASE WHEN SB_AD.DOC_APPR_DATE IS NULL THEN '진행중' WHEN (SELECT COUNT(*) FROM APPROVAL_LIST WHERE DOC_CODE = D.DOC_CODE AND APPR_STATUS = 'C') > 0 THEN '반려' ELSE '완료' END FROM APPROVAL_DOC SB_AD WHERE SB_AD.DOC_CODE = D.DOC_CODE ) AS STATUS FROM APPROVAL_DOC D INNER JOIN DOC_FORM F ON D.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = D.EMP_CODE WHERE D.DOC_STATUS = 'N' AND D.EMP_CODE = #{empCode} AND D.DOC_APPR_DATE IS NOT NULL
AND D.DOC_FORM_CODE = #{docFormCode}
ORDER BY D.DOC_CODE DESC

SELECT DISTINCT(A.DOC_CODE) AS DOC_CODE, A.DOC_FORM_CODE, A.DOC_TITLE, A.EMP_CODE, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, E.EMP_NAME, F.FORM_NAME , ( SELECT CASE WHEN SB_AD.DOC_APPR_DATE IS NULL THEN '진행중' WHEN (SELECT COUNT(*) FROM APPROVAL_LIST WHERE DOC_CODE = A.DOC_CODE AND APPR_STATUS = 'C') > 0 THEN '반려' ELSE '완료' END FROM APPROVAL_DOC SB_AD WHERE SB_AD.DOC_CODE = A.DOC_CODE ) AS STATUS FROM APPROVAL_DOC A INNER JOIN DOC_FORM F ON A.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE INNER JOIN APPROVAL_LIST L ON L.DOC_CODE = A.DOC_CODE WHERE L.APPR_EMP_CODE = #{empCode} AND A.DOC_STATUS = 'N' AND A.DOC_APPR_DATE IS NOT NULL
AND A.DOC_FORM_CODE = #{docFormCode}
ORDER BY A.DOC_CODE DESC

SELECT DISTINCT(A.DOC_CODE) AS DOC_CODE, A.DOC_FORM_CODE, A.DOC_TITLE, A.EMP_CODE, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, E.EMP_NAME, F.FORM_NAME , ( SELECT CASE WHEN SB_AD.DOC_APPR_DATE IS NULL THEN '진행중' WHEN (SELECT COUNT(*) FROM APPROVAL_LIST WHERE DOC_CODE = A.DOC_CODE AND APPR_STATUS = 'C') > 0 THEN '반려' ELSE '완료' END FROM APPROVAL_DOC SB_AD WHERE SB_AD.DOC_CODE = A.DOC_CODE ) AS STATUS FROM APPROVAL_DOC A INNER JOIN DOC_FORM F ON A.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE INNER JOIN APPROVAL_REF R ON R.DOC_CODE = A.DOC_CODE WHERE R.REF_EMP_CODE = #{empCode} AND A.DOC_STATUS = 'N' AND A.DOC_APPR_DATE IS NOT NULL
AND A.DOC_FORM_CODE = #{docFormCode}
ORDER BY A.DOC_CODE DESC

SELECT DISTINCT(A.DOC_CODE) AS DOC_CODE, A.DOC_FORM_CODE, A.DOC_TITLE, A.EMP_CODE, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, E.EMP_NAME, F.FORM_NAME , ( SELECT CASE WHEN SB_AD.DOC_APPR_DATE IS NULL THEN '진행중' WHEN (SELECT COUNT(*) FROM APPROVAL_LIST WHERE DOC_CODE = A.DOC_CODE AND APPR_STATUS = 'C') > 0 THEN '반려' ELSE '완료' END FROM APPROVAL_DOC SB_AD WHERE SB_AD.DOC_CODE = A.DOC_CODE ) AS STATUS FROM APPROVAL_DOC A INNER JOIN DOC_FORM F ON A.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE INNER JOIN APPROVAL_LIST L ON L.DOC_CODE = A.DOC_CODE WHERE A.EMP_CODE = #{empCode} AND A.DOC_STATUS = 'N' AND L.APPR_STATUS = 'C' AND A.DOC_APPR_DATE IS NOT NULL
AND A.DOC_FORM_CODE = #{docFormCode}
ORDER BY A.DOC_CODE DESC

SELECT COUNT(*) FROM APPROVAL_DOC
WHERE DOC_STATUS = 'N'

	AND DOC_FORM_CODE = #{docFormCode}

SELECT DISTINCT(A.DOC_CODE) AS DOC_CODE, A.DOC_FORM_CODE, A.DOC_TITLE, A.EMP_CODE, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, E.EMP_NAME, F.FORM_NAME , ( SELECT CASE WHEN SB_AD.DOC_APPR_DATE IS NULL THEN '진행중' WHEN (SELECT COUNT(*) FROM APPROVAL_LIST WHERE DOC_CODE = A.DOC_CODE AND APPR_STATUS = 'C') > 0 THEN '반려' ELSE '완료' END FROM APPROVAL_DOC SB_AD WHERE SB_AD.DOC_CODE = A.DOC_CODE ) AS STATUS FROM APPROVAL_DOC A INNER JOIN DOC_FORM F ON A.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE WHERE A.DOC_STATUS = 'N'
AND A.DOC_FORM_CODE = #{docFormCode}
ORDER BY A.DOC_CODE DESC

SELECT COUNT(*) FROM APPROVAL_DOC
WHERE DOC_STATUS = 'D'

	AND DOC_FORM_CODE = #{docFormCode}

SELECT DISTINCT(A.DOC_CODE) AS DOC_CODE, A.DOC_FORM_CODE, A.DOC_TITLE, A.EMP_CODE, TO_CHAR(A.DOC_ENROLL_DATE, 'YYYY.MM.DD') AS DOC_ENROLL_DATE, E.EMP_NAME, F.FORM_NAME, A.DOC_STATUS FROM APPROVAL_DOC A INNER JOIN DOC_FORM F ON A.DOC_FORM_CODE = F.FORM_CODE INNER JOIN EMP E ON E.EMP_CODE = A.EMP_CODE WHERE A.DOC_STATUS = 'D'
AND A.DOC_FORM_CODE = #{docFormCode}
ORDER BY A.DOC_CODE DESC
