mapper.insert:
INSERT INTO CONTACTS 
(
	CON_NO
	, CON_WRITER
	, CON_NAME
	, CON_EMAIL
	, CON_TEL
	, CON_COMPANY
	, CON_MEMO
)
VALUES
(
	SEQ_CONTACTS_NO.NEXTVAL
	, #{conWriter}
	, #{conName}
	, #{conEmail}
	, #{conTel}
	, #{conCompany}
	, #{conMemo}
)

mapper.update:
	UPDATE CONTACTS
			SET
             CON_NAME = #{conName}   
            , CON_EMAIL = #{conEmail}        
            , CON_TEL = #{conTel}           
            , CON_COMPANY = #{conCompany} 
            , CON_MEMO = #{conMemo}       
			WHERE CON_NO = #{conNo}
