mapper.select:
SELECT 
	EMP_CODE
  , EMP_ID
FROM EMP
WHERE EMP_NAME  = #{empName}
AND EMP_PHONE = #{empPhone}

SELECT 
	EMP_CODE,
	EMP_PWD
FROM EMP 
WHERE EMP_NAME = #{empName}
AND EMP_E_MAIL = #{empEMail}

mapper.update:
UPDATE EMP
 SET 
 	EMP_TEL = #{empTel}
	, EMP_PHONE = #{empPhone}
	, EMP_E_MAIL = #{empEMail}
	, EMP_ADDRESS1 = #{empAddress1}
	, EMP_ADDRESS2 = #{empAddress2}
	, EMP_PROFILE_NAME = #{empProfileName}
	, EMP_FILE_NAME = #{empFileName}

		, EMP_PWD = #{empPwd}

WHERE EMP_CODE = #{empCode}
