mapper.select:
SELECT COUNT (*)
FROM MAIL
WHERE MAIL_DELETE IS NULL
AND MAIL_RECEIVER = #{id}
AND MAIL_SENDER != #{id}

SELECT COUNT (*)
FROM MAIL
WHERE MAIL_DELETE IS NULL
AND MAIL_SENDER= #{id}
AND MAIL_RECEIVER != #{id}

SELECT COUNT (*)
FROM MAIL
WHERE MAIL_DELETE IS NULL
AND MAIL_SENDER= #{id}
AND MAIL_RECEIVER = #{id}

mapper.insert:
INSERT INTO MAIL 
(
MAIL_CODE
, EMP_CODE
, MAIL_SENDER
, MAIL_TITLE
, MAIL_CONTENT
, MAIL_SENDDATE
, MAIL_RECEIVER
)
VALUES 
(
SEQ_MAIL_CODE.NEXTVAL
 ,#{empCode}
 ,#{mailSender}
 ,#{mailTitle}
 ,#{mailContent}
 ,SYSDATE
 ,#{mailReceiver}
 )

INSERT INTO MAIL_FILE
(
 MAIL_FILE_CODE
 , MAIL_CODE
 , MAIL_ORIGIN_NAME
 , MAIL_CHANGE_NAME
 , MAIL_FILE_ROOT
)
VALUES
(
 SEQ_MAIL_FILE_CODE.NEXTVAL
 ,SEQ_MAIL_CODE.CURRVAL
 ,#{mailOriginname}
 ,#{mailChangename}
 ,#{mailFileroot}
)

mapper.select:
SELECT MAIL_FILE_CODE, MAIL_CODE, MAIL_ORIGIN_NAME, MAIL_CHANGE_NAME,MAIL_FILE_ROOT FROM MAIL_FILE
WHERE MAIL_CODE = #{mailCode}
ORDER BY MAIL_FILE_CODE ASC

mapper.delete:
DELETE 
FROM MAIL WHERE MAIL_DELETE IS NOT NULL
AND MAIL_CODE = #{mailCode}

mapper.select:
